Alias.script:
if synContinue then
	synContinue = false
	echo("\n\t\tSYNTHESIS PAUSED\n")
elseif transContinue then
	transContinue = false
	echo("\n\t\tTRANSMUTATION PAUSED\n")
else
	echo("\n\t\tNO SYNTHESIS IN PROCESS\n")
end

synContinue = true enableTrigger("Synthesis Continue") synMetal = matches[2] local synDesired = math.floor((cureRiftMax-riftList[synMetal])/2) if synMetal == "gold" or synMetal == "silver" then synBase = "mercury" elseif synMetal == "iron" or synMetal == "lead" then synBase = "salt" elseif synMetal == "tin" or synMetal == "copper" then synBase = "sulphur" end
if synDesired < riftList[synBase] then synLeft = synDesired - 1 else synLeft = riftList[synBase] - 1 end
echo("\n\t\tSynthesising " .. synLeft .. " times!\n") Synthesise(synMetal, synBase)

Script.script:
function variableInit()
	math.randomseed(os.time())

	riftList = {}
	cureRiftMax = 2500 --herb, commodity, reagent, crystal
	inkRiftMax = 750 --ink, ingredient
	healthRiftMax = 2000 --health, mana
	sipRiftMax = 1000 --other fluids

	maxHP = tonumber(gmcp.Char.Vitals.maxhp)
	maxMP = tonumber(gmcp.Char.Vitals.maxmp)

	hasWord = true
	hasBal = true
	hasEqu = true
	sipBal = true
	potashBal = true
	didSit = false

	invList  = {}
	roomList = {}

	wordQueue = {}
	balQueue = {}
	equQueue = {}

	riftList = {}
	afflictList = {}
	eatQueue = {}

	extractedRooms = {}
	
	plantToMineral = 
	{
	ginseng = "ferrum",
	lobelia = "argentum",
	kelp = "aurum",
	ash = "stannum",
	goldenseal = "plumbum",
	pear = "calcite",
	bellwort = "cuprum",
	bloodroot = "magnesium",
	ginger = "antimony"
	}
	
end

function addToSet(set, key)
    set[key] = true
end

function removeFromSet(set, key)
    set[key] = nil
end

function setContains(set, key)
    return set[key] ~= nil
end

function removeAfflict()
	table.remove(afflictList, 1)
	echo(gmcp.Char.Afflictions.Remove[1] .. " cured!")
end

Key.script:
if not extracting then
	extracting = true
	echo("\t\tEXTRACTION STARTED\n")
else
	extracting = false
	extractedRooms = {}
	echo("\t\tEXTRACTION FINISHED\n")
end
